
public class DeclaracionesOperadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		final double apulgadas=2.54;
		
		// eso de "final" significa que es una constante y no una variable
		
		double cm=6;
		
		double resultado=cm/apulgadas;
		
		System.out.println("En " + cm + "cm hay " + resultado + " pulgadas");
		
		// Este es un ejemplo de como se usa la CONCATENACIÃ“N, que es el unir valores
		
		int operador1,operador2, result;
		
		operador1=5;
		operador2=20;
		result=operador1+operador2;
		
		System.out.println(result); 
		
	}

}
